mapper.select:
SELECT u.user_id AS id, u.tenant_id AS tenantId, u.user_id AS userId, u.name AS userName, u.email, u.password, u.role, u.created_at AS createAt, t.is_active AS isActive, t.name AS tenantName, t.sub_domain AS subDomain FROM user u INNER JOIN tenant t ON u.tenant_id = t.tenant_id WHERE u.tenant_id = #{tenantId}
AND (u.name LIKE CONCAT('%', #{searchKeyword}, '%') OR u.email LIKE CONCAT('%', #{searchKeyword}, '%'))
ORDER BY u.created_at DESC

mapper.update:
UPDATE user SET
name = #{userName},
email = #{email},

    password = #{password},

role = #{role}
WHERE user_id = #{userId}
AND tenant_id = #{tenantId}
