mapper.select:
SELECT user_id AS id, tenant_id, name AS userName, email, password, role
FROM user
WHERE email = #{email}

SELECT u.user_id AS id, u.tenant_id AS tenantId, u.name AS userName, u.email, u.role, u.created_at AS createAt, u.user_id AS userId, t.is_active AS isActive, t.name AS tenantName, t.sub_domain AS subDomain FROM user u JOIN tenant t ON u.tenant_id = t.tenant_id WHERE u.tenant_id = #{tenantId}
AND (u.name LIKE CONCAT('%', #{searchKeyword}, '%') OR u.email LIKE CONCAT('%', #{searchKeyword}, '%'))
ORDER BY u.created_at DESC

mapper.update:
UPDATE user SET name = #{userName}, email = #{email},
password = #{password},
role = #{role}, updated_at = NOW() WHERE user_id = #{userId} AND tenant_id = #{tenantId}

mapper.delete:
DELETE FROM user
WHERE user_id = #{userId}

    AND tenant_id = #{tenantId}
